// Dependencies
import React, { Component } from "react";
import { Link } from "react-router-dom";
import Utils from "../utils/utils";

// Redux
import PropTypes from "prop-types";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";

// Material UI
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

// Custom Actions


// START IMPORT ACTIONS
import ProductActions from "../redux/actions/ProductActions";

// END IMPORT ACTIONS

/** APIs

* actionsProduct.create
*	@description CRUD ACTION create
*
* actionsProduct.update
*	@description CRUD ACTION update
*	@param ObjectId id - Id
*
* actionsProduct.get
*	@description CRUD ACTION get
*	@param ObjectId id - Id resource
*

**/

class ProductEdit extends Component {
  // Init product
  constructor(props) {
    super(props);
    this.state = {
      product: {}
    };
  }

  // Load data on start
  componentDidMount() {
    if (this.props.match.params.id !== "new") {
      this.props.actionsProduct.loadProduct(this.props.match.params.id);
    }
    
  }

  // Insert props product in state
  componentWillReceiveProps(props) {
    this.setState(...this.state, {
      product: props.product
    });
  }

  // Save data
  save(event) {
    event.preventDefault();
    if (this.state.product._id) {
      this.props.actionsProduct.saveProduct(this.state.product).then(data => {
        this.props.history.push("/products/");
      });
    } else {
      this.props.actionsProduct.createProduct(this.state.product).then(data => {
        this.props.history.push("/products/");
      });
    }
  }

  // Show content
  render() {
    return (
      <div>
        <h1>Product Edit</h1>
        <form className="myForm" onSubmit={this.save.bind(this)}>

          
          <TextField
            id="category"
            label="Category"
            value={this.state.product.category || ""}
            onChange={Utils.handleChange.bind(this, "product")}
            margin="normal"
            fullWidth
          />
          
          
          <TextField
            id="product_name"
            label="Product_name"
            value={this.state.product.product_name || ""}
            onChange={Utils.handleChange.bind(this, "product")}
            margin="normal"
            fullWidth
            required
            {...(!this.state.product.product_name && this.state.product.product_name === ""
              ? { error: true }
              : {})}
          />
          
          
          <TextField
            id="quantity"
            label="Quantity"
            value={this.state.product.quantity || ""}
            onChange={Utils.handleChange.bind(this, "product")}
            type="number"
            margin="normal"
            fullWidth
          />
          

          {/* Footer */}
          <div className="footer-card">
            <Link to="/products/">Back to list</Link>

            <Button type="submit" variant="contained" color="primary">
              Save
            </Button>
          </div>
        </form>
      </div>
    );
  }
}

// Store actions
const mapDispatchToProps = function(dispatch) {
  return { 
    actionsProduct: bindActionCreators(ProductActions, dispatch),
  };
};

// Validate types
ProductEdit.propTypes = { 
  actionsProduct: PropTypes.object.isRequired,
};

// Get props from state
function mapStateToProps(state, ownProps) {
  return {
    product: state.ProductEditReducer.product
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProductEdit);
